INSERT INTO [Category] ([CategoryID], [Name], [Description], [Image]) VALUES (1, N'Váy / Đầm', N'Váy là một loại trang phục phổ biến và đa dạng, được thiết kế để che phủ và làm đẹp cho cơ thể người mặc. Có nhiều loại váy khác nhau, phục vụ cho các dịp và sự kiện khác nhau, từ hàng ngày đến dịp đặc biệt.', N'/categories/f85728e6-2483-494b-9889-ed35632f80d6/preview.jpg'); GO
INSERT INTO [Category] ([CategoryID], [Name], [Description], [Image]) VALUES (2, N'T-Shirt', N'Áo T-shirt là một kiểu áo phổ biến và thoải mái, thường được làm từ chất liệu cotton, poly-cotton blend, hoặc các loại vải co giãn khác. Áo T-shirt không chỉ là một mảnh trang phục hàng ngày mà còn là biểu tượng văn hóa, thể hiện cái tôi và phong cách cá nhân của người mặc.', N'/categories/14bc500c-81a7-48f5-9eca-a3aa8d6e2152/preview.jpg'); GO
INSERT INTO [Category] ([CategoryID], [Name], [Description], [Image]) VALUES (3, N'Vest', N'Vest không chỉ thêm phần lịch lãm và chính thức vào trang phục mà còn là một phần của thế giới thời trang có tính biến động và đa dạng. Vest là một phần trang phục có thể thể hiện phong cách cá nhân và sự sáng tạo thông qua lựa chọn về màu sắc, chi tiết, và phối hợp với các phụ kiện khác.', N'/categories/fae239ef-ce97-4a08-9304-31f3c295b430/preview.jpg'); GO
INSERT INTO [Category] ([CategoryID], [Name], [Description], [Image]) VALUES (4, N'Hoodie', N'Hoodie được thiết kế để mang lại sự thoải mái và ấm áp, là trang phục lựa chọn phổ biến cho các hoạt động ngoại ô, thể thao, hoặc khi ở nhà. Hoodie không chỉ là một mảnh trang phục thoải mái mà còn là biểu tượng của phong cách đô thị và cái tôi cá nhân, thường được ưa chuộng trong thời trang đường phố và văn hóa thanh niên. ', N'/categories/1cca34c8-8e93-4791-ad29-ddd5919fde8e/preview.webp'); GO
INSERT INTO [Category] ([CategoryID], [Name], [Description], [Image]) VALUES (5, N'Áo len', N'Áo len thường mang đến vẻ ngoài đơn giản, ấm áp và thời trang. Có thể được kết hợp với nhiều phong cách khác nhau, từ trang phục hàng ngày đến trang phục chính thức. Áo len có thể có các chi tiết như viền bo, các đường may độc đáo, hoặc các chi tiết trang trí để tạo điểm nhấn và tăng tính thẩm mỹ.', N'/categories/22939d28-043e-46bc-98b3-e5c8d181d27b/preview.jpg'); GO
